    private void btnBackActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBackActionPerformed
         
    LoginScreen loginScreen = new LoginScreen(BIM, workPanel);
    
    
    // Add the login screen panel back to the workPanel
    workPanel.add("LoginScreen", loginScreen);
    
    // Switch to the login screen panel
    CardLayout layout = (CardLayout) workPanel.getLayout();
    layout.show(workPanel, "LoginScreen");
    }//GEN-LAST:event_btnBackActionPerformed

    private void btnVINDirectoryActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVINDirectoryActionPerformed
        // Instantiate the VINAuditMainScreen panel and pass BIM
        
    VIN_DB_MainScreen vinAuditMainScreen = new VIN_DB_MainScreen(workPanel);
    
    // Remove the current panel from the workPanel
    workPanel.remove(this);
    
    // Add the VINAuditMainScreen panel back to the workPanel
    workPanel.add("VINAuditMainScreen", vinAuditMainScreen);
    
    // Switch to the VINAuditMainScreen panel
    CardLayout layout = (CardLayout) workPanel.getLayout();
    layout.previous(workPanel);
    }//GEN-LAST:event_btnVINDirectoryActionPerformed


