    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        // TODO add your handling code here:
    String enteredUsername = txtUserName.getText().trim();
    String enteredPassword = new String(txtPassword.getPassword());

    // Validate input fields
    if (enteredUsername.isEmpty() || enteredPassword.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Username and Password cannot be empty.", "Error", JOptionPane.ERROR_MESSAGE);
        return;
    }

    // Query to check credentials
    String query = "SELECT * FROM users WHERE username = ? AND password = ? AND role = 'POLK'";

    try (Connection conn = DatabaseConnection.getConnection();
         PreparedStatement stmt = conn.prepareStatement(query)) {

        stmt.setString(1, enteredUsername);
        stmt.setString(2, enteredPassword);

        ResultSet rs = stmt.executeQuery();

        if (rs.next()) {
            // Valid credentials
            txtUserName.setText("");
            txtPassword.setText("");

            // Create an instance of POLKChoiceScreen
            POLKChoiceScreen polkChoiceScreen = new POLKChoiceScreen(workPanel);

            // Add POLKChoiceScreen to the workPanel
            workPanel.add("POLKChoiceScreen", polkChoiceScreen);

            // Switch to POLKChoiceScreen
            CardLayout layout = (CardLayout) workPanel.getLayout();
            layout.next(workPanel);

        } else {
            // Invalid credentials
            txtPassword.setText("");
            JOptionPane.showMessageDialog(this, "Invalid credentials or user does not have POLK role.", "Error", JOptionPane.ERROR_MESSAGE);
        }

    } catch (Exception e) {
        e.printStackTrace();
        JOptionPane.showMessageDialog(this, "Database error: " + e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
    }
    }//GEN-LAST:event_btnLoginActionPerformed

    private void btnMainScreenActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMainScreenActionPerformed
        // TODO add your handling code here:
        workPanel.remove(this);
        CardLayout layout = (CardLayout) workPanel.getLayout();
        layout.previous(workPanel);
   
    }//GEN-LAST:event_btnMainScreenActionPerformed


